/*global alert, console, $, _gaq *//**
 * Definition of the Device object which controls all
 * functionality related specifically to the user's device.
 *
 * @require jquery.js
 */var Device={type:"desktop",width:0,height:0,scrollTop:0,init:function(){this.setDeviceType();this.setDimentions();this.setScrollTop();$(window).resize(function(){Device.setDimentions()});$(window).scroll(function(){Device.setScrollTop()})},setDeviceType:function(){var e=navigator.userAgent.toLowerCase();if(e.match(/(iphone|ipod|ipad|android|blackberry)/))if(e.match(/(ipad)/)){this.type="tablet";this.setViewport(this.type)}else{this.type="phone";this.setViewport(this.type)}else{this.type="desktop";this.setViewport(this.type)}},setViewport:function(e){switch(e){case"tablet":break;case"phone":break;default:}},setDimentions:function(){this.width=$(window).width();this.height=$(window).height()},setScrollTop:function(){this.scrollTop=$(window).scrollTop()}};$(function(){Device.init()});